using Microsoft.AspNetCore.Http;
using Microsoft.AspNetCore.Mvc;
using Shop.Models;

namespace Shop.Controllers
{
    public class ShopController : Controller
    {
        public static IList<ShopItem> shopItems = new List<ShopItem> {
            new ShopItem() { Id=1, Name="Avocado From Mexico", Description="The fruit of domestic varieties have smooth, buttery, golden-green flesh when ripe. Depending on the cultivar, avocados have green, brown, purplish, or black skin, and may be pear-shaped, egg-shaped, or spherical. For commercial purposes the fruits are picked while unripe and ripened after harvesting.", Price=2, imgURL = "data:image/jpeg;base64,"},
            new ShopItem() { Id=2, Name="Peach", Description="A peach is a soft, round, slightly furry fruit with sweet yellow flesh and pinky-orange skin. Peaches grow in warm countries. Something that is peach is pale pinky-orange in colour.", imgURL="data:image/jpeg;base64,", Price=4},
            new ShopItem() { Id=3, Name="Banana", Description="Bananas are long, curved fruits with smooth, yellow, and sometimes slightly green skin. The average length of a banana is about 7 to 9 inches, and it is about 2 to 3 inches in diameter. The skin of the banana is usually yellow when it is ripe, but it can also be green, red, or purple depending on the variety.", Price=3, imgURL="data:image/jpeg;base64,"}
        };
        public IActionResult ShopList()
        {
            return View(shopItems);
        }
        // GET: ShopController
        public ActionResult Index()
        {
            return View(shopItems);
        }

        // GET: ShopController/Details/5
        public ActionResult Details(int id)
        {
            ShopItem item = shopItems.FirstOrDefault(x => x.Id == id);
            ViewBag.ItemName = item.Name;
            return View(shopItems.FirstOrDefault(x => x.Id == id));
        }

        // GET: ShopController/Create
        public ActionResult Create()
        {
            return View();
        }

        // POST: ShopController/Create
        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Create(ShopItem shopItem)
        {
            shopItem.Id = shopItems.Count + 1;
            shopItems.Add(shopItem);
            return RedirectToAction("Index");
        }

        // GET: ShopController/Edit/5
        public ActionResult Edit(int id)
        {
            return View(shopItems.FirstOrDefault(x => x.Id == id));
        }

        // POST: ShopController/Edit/5
        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Edit(int id, IFormCollection collection)
        {
            try
            {
                return RedirectToAction(nameof(Index));
            }
            catch
            {
                return View();
            }
        }

        // GET: ShopController/Delete/5
        public ActionResult Delete(int id)
        {
            return View(shopItems.FirstOrDefault(x => x.Id == id));
        }

        // POST: ShopController/Delete/5
        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult Delete(int id, ShopItem shopItem)
        {
            ShopItem shopItemToDelete = shopItems.FirstOrDefault(x => x.Id == id);
            shopItems.Remove(shopItemToDelete);
            return RedirectToAction("Index");
        }
    }
}
